import algoliasearch from 'algoliasearch/lite';
import { InstantSearch, SearchBox, Hits, Highlight, connectRefinementList } from 'react-instantsearch-dom';

const searchClient = algoliasearch('GF9DTO0HM0', 'daf7d7e0cc8a9cdc3f34fbd6e36197d9');

searchClient.search('').then(({ value }) => {
  console.log(value);
});

const Hit = ({ hit }) => (
  <p>
    <Highlight attribute="name" hit={hit} tagName="name" />
  </p>
);



const App = () => (
  <InstantSearch indexName="furniture_records" searchClient={searchClient}>
    <SearchBox />
    <StaticRefinementList
      attribute="category"
      values={[
        { label: 'iPad', value: 'iPad' },
        { label: 'iPhone', value: 'iPhone' },
      ]}
    />
    <Hits hitComponent={Hit} />
  </InstantSearch>
);

const StaticRefinementList = connectRefinementList(
  ({ values, currentRefinement, items, refine }) => (
    <ul className="ais-RefinementList-list">
      {values.map(staticItem => {
        const { isRefined } = items.find(
          item => item.label === staticItem.label
        ) || {
          isRefined: false,
        };

        return (
          <li key={staticItem.value}>
            <label>
              <input
                type="checkbox"
                value={staticItem.value}
                checked={isRefined}
                onChange={event => {
                  const value = event.currentTarget.value;
                  const next = currentRefinement.includes(value)
                    ? currentRefinement.filter(current => current !== value)
                    : currentRefinement.concat(value);

                  refine(next);
                }}
              />
              {staticItem.label}
            </label>
          </li>
        );
      })}
    </ul>
  )
);


export default App;
