import algoliasearch from 'algoliasearch/lite';
import { InstantSearch, SearchBox, Hits, Highlight } from 'react-instantsearch-dom';

// const searchClient = algoliasearch(
//   'GF9DTO0HM0',
//   'daf7d7e0cc8a9cdc3f34fbd6e36197d9'
// );

const searchClient = algoliasearch('GF9DTO0HM0', 'daf7d7e0cc8a9cdc3f34fbd6e36197d9');

const Hit = ({ hit }) => (
  <p>
    <Highlight attribute="name" hit={hit} tagName="furniture_records" />
  </p>
);

const App = () => (
  <InstantSearch indexName="furniture_records" searchClient={searchClient}>
    <SearchBox />
    <Hits hitComponent={Hit} />
  </InstantSearch>
);

export default App;
